function calcular(c){if(c.id=="valorGasolina"){var g=document.getElementById("valorAlcool").value.replace(",",".");var d=c.value.replace(",",".")}else{var g=c.value.replace(",",".");var d=document.getElementById("valorGasolina").value.replace(",",".")}var b=new Date();var f=b.toLocaleString();localStorage.setItem("old_valorGasolina",d.replace(".",","));localStorage.setItem("old_valorAlcool",g.replace(".",","));localStorage.setItem("old_data",f);var a=document.getElementById("result");var e=parseFloat(g/d);if(e<=0.7){a.innerHTML="Abasteça com <b>ÁLCOOL</b>";a.style.backgroundColor="#43A047"}else{a.innerHTML="Abasteça com <b>GASOLINA</b>";a.style.backgroundColor="#EF5350"}}function logValor(){var a="",c="",d="";if(localStorage.getItem("old_valorGasolina")&&localStorage.getItem("old_valorAlcool")&&localStorage.getItem("old_data")){a=localStorage.getItem("old_valorGasolina");c=localStorage.getItem("old_valorAlcool");d=localStorage.getItem("old_data")}var b=document.getElementById("old_result");if(a!=""&&c!=""&&d!=""){b.style.display="block"}b.innerHTML="Em "+d+" o valor da Gasolina era de R$"+a+" e o Álcool era de R$"+c+"."};